mapper.update:
update ums_product
set prodname=#{prodname}, prodprice=#{prodprice}, prodamount=#{prodamount},
prodinf=#{prodinfo}, prodcategory=#{prodcategory}
where prodnum=#{prodnum}

update ums_product
set likecnt=likecnt+1
where prodnum=#{prodnum}

mapper.select:
select * from ums_product


		prodcategory=#{cri.category}


order by prodnum desc limit #{startrow}, #{cri.amount}
